import ChartText from "../Text.vue";
import ProgressBar from "../ProgressBar.vue";
import Expander from "../../Expander.vue";
import taskMixin from "./Task.mixin.js";
export default {
  name: 'Milestone',
  components: {
    ChartText: ChartText,
    ProgressBar: ProgressBar,
    Expander: Expander
  },
  inject: ['root'],
  props: ['task'],
  mixins: [taskMixin],
  data: function data() {
    return {};
  },
  computed: {
    /**
     * Get clip path id
     *
     * @returns {string}
     */
    clipPathId: function clipPathId() {
      return 'gantt-elastic__milestone-clip-path-' + this.task.id;
    },

    /**
     * Get points
     *
     * @returns {string}
     */
    getPoints: function getPoints() {
      var task = this.task;
      var fifty = task.height / 2;
      var offset = fifty;

      if (task.width / 2 - offset < 0) {
        offset = task.width / 2;
      }

      return "0,".concat(fifty, "\n        ").concat(offset, ",0\n        ").concat(task.width - offset, ",0\n        ").concat(task.width, ",").concat(fifty, "\n        ").concat(task.width - offset, ",").concat(task.height, "\n        ").concat(offset, ",").concat(task.height);
    }
  }
};
